var MatchTimer = function(obj) {
	this.$matchObj = $(obj);
	var matchWidthObj = [];
	
	this.setMatchWidthObj = function(obj) {
		matchWidthObj = [];
		if(obj instanceof Array) {
			for (var i = 0; i < obj.length; i++){
				matchWidthObj.push($(obj[i]));}
		} else {
			matchWidthObj.push($(obj));
		}
	};
	
	var getMatchWidth = function() {
		var matchWidth = 0;
		if(matchWidthObj instanceof Array) {
			for (var i = 0; i < matchWidthObj.length; i++)
				matchWidth += matchWidthObj[i].outerWidth();
		}
		
		return matchWidth;
	};
	
	this.showMatch = function(left,time, extraSize) {
		extraSize = typeof extraSize === undefined ? 0 : extraSize;
		var newWidth = 0;
		newWidth = getMatchWidth() + extraSize;
		this.$matchObj.width(newWidth).animate({
			'left' : left
		}, time);
	};
	
};
/**
 * Countdown By: Diego Incerti
 * http://diegoincerti.com.br/
 *
 * Copyright (c) 2014 Diego Incerti
 * Licensed under the GPLv2+ license.
 */

(function($) {
	$.fn.countdown = function(settings) {
		var config = {
			'delay' : 1000,
			'offsetIndex' : 2
		};
		if (settings) {
			$.extend(config, settings);
		}

		// variables
		var obj = $(this);
		var elements = obj.find('> *');
		var elementIndex = elements.length - 1;
		var childrenIndex = elements.first().find('> *').length - config.offsetIndex;
		var maxValue = 9;

		/**
		 * Decrement the numbers usin recursion
		 *
		 * var elementIndex for the day, hour, min, sec.. container
		 * var childrenIndex for the unit, dozens elements
		 *
		 * note: the hour needs to have a class .hour
		 */
		function decrement(elementIndex, childrenIndex) {
			
			// right value for minutes and seconds
			maxValue = childrenIndex == 1 ? 9 : 5;
			
			// right value for hour
			if (elements.eq(elementIndex).hasClass('hour')) {
				if (childrenIndex == 1 && elements.eq(elementIndex).find('> *').eq(childrenIndex - 1).html() < 1)
					maxValue = 3;
				if (childrenIndex == 0)
					maxValue = 2;
			}
			
			// current element value
			currentValue = elements.eq(elementIndex).find('> *').eq(childrenIndex).html();
			
			if (currentValue == 0) {
				
				currentValue = maxValue;
				elements.eq(elementIndex).find('> *').eq(childrenIndex).html(currentValue);
				
				if (childrenIndex == 0) {
					
					if (elementIndex != 0)
						elementIndex--;
						
					childrenIndex++;

				} else
					childrenIndex--;

				decrement(elementIndex, childrenIndex);

			} else {
				currentValue--;
				elements.eq(elementIndex).find('> *').eq(childrenIndex).html(currentValue);
			}

			return;
		}

		// run
		setInterval(function() {
			// valid if the time it's not equal to 0
			for(i=0;i<=elementIndex;i++){
				for(j=0;j<=childrenIndex;j++){
					if(elements.eq(i).find('> *').eq(j).html() != 0){
						// call decrement function
						decrement(elementIndex, childrenIndex);
						return 0;
					}
				}
			}
		}, config.delay);

		return this;

	};
})(jQuery);

/**
 * Ajax Post Loader By: Diego Incerti
 * http://diegoincerti.com.br/
 *
 * Copyright (c) 2014 Diego Incerti
 * Licensed under the GPLv2+ license.
 */

(function($) {
	$.fn.postLoader = function(settings) {
		// Default configs
		var config = {
			'trigger' : '.btn',
			'contentAppend' : false,
			'postsNumber' : 1,
			'page' : 1,
			'maxLoads' : 5,
			'animationDuration' : 500,
			'baseUrl' : '',
			'template' : '',
			'type' : 'GET',
			'loading' : true
		};
		// Set configs
		if (settings) {
			$.extend(config, settings);
		}
		// Variables
		var $content = $(this);
		var $trigger = $content.find(config.trigger);
		// Set contente to return data
		config.contentAppend = config.contentAppend ? $content.find(config.contentAppend) : $content;
		
		/*
		 * Get the relative path of template
		 */
		var backUrl = '';
		var url = window.location.protocol + "//" + window.location.host + window.location.pathname;
		url = url.replace(config.baseUrl, '');
		url = url.split('/');
		for(var i = url.length-2;i>0;i--)
			backUrl += '../';
		config.template = backUrl + config.template;
		
		/*
		 * Ajax Function
		 */
		var load_posts = function() {
			$.ajax({
				type : config.type,
				data : {
					numPosts : config.postsNumber,
					pageNumber : config.page,
				},
				dataType : "html",
				url : config.template,
				success : function(data) {
					$data = $(data);
					$data.hide();
					config.contentAppend.append($data);
					$data.fadeIn(config.animationDuration, function() {
						config.loading = false;
					});
					if (config.page > config.maxLoads)
						$trigger.hide();
				},
				error : function(jqXHR, textStatus, errorThrown) {
					console.log(jqXHR + " :: " + textStatus + " :: " + errorThrown);
				}
			});
		};
		$trigger.click(function(e) {
			e.preventDefault();
			if (!config.loading) {
				config.loading = true;
				config.page++;
				load_posts();
			}
		});
		if(config.loading)
			load_posts();
	};
})(jQuery);

var Team = function(elementDescriptionObj) {
	this.descriptionObj = elementDescriptionObj;
	this.carouselObj = '';
	var ajaxUrl = '';
	var playerId = '';

	this.setUrl = function(newUrl) {
		ajaxUrl = newUrl;
	};

	this.setId = function(newid) {
		playerId = newid;
	};

	this.setCarousel = function(newCarouselObj) {
		this.carouselObj = newCarouselObj;
	};

	this.getDescription = function() {
		var descriptionObj = this.descriptionObj;
		descriptionObj.fadeTo('slow', 0.75).addClass('no-click');
		$.ajax({
			type : 'POST',
			url : ajaxUrl,
			dataType : 'json',
			data : {
				id : playerId
			},
			cache : false,
			success : function(data) {
				data.id = data.id ? data.id : '';
				data.number = data.number ? data.number : '';
				data.name = data.name ? data.name : '';
				data.position = data.position['name'] && data.position['description'] ? data.position['name'] + ' (' + data.position['description'] + ')' : '';
				descriptionObj.find('.number').html(data.number);
				descriptionObj.find('.name').html(data.name);
				descriptionObj.find('.position').html(data.position);
				descriptionObj.find('.see-statistics a').attr('href', '#' + data.id).end().fadeTo("slow", 1).removeClass('no-click');
			},
			error : function(jqXHR, textStatus, errorThrown) {
				console.log(textStatus, errorThrown);
			}
		});
	};
};

